// Unified data model\npublic class Customer\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public virtual ICollection<Order> Orders { get; set; }\n}\n\npublic class Order\n{\n    public int Id { get; set; }\n    public int CustomerId { get; set; }\n    public virtual Customer Customer { get; set; }\n}\n\n// Bounded context 1: Customer Management\npublic class CustomerContext : DbContext\n{\n    public DbSet<Customer> Customers { get; set; }\n    public DbSet<Order> Orders { get; set; } // Only include orders for customer management\n}\n\n// Bounded context 2: Order Processing\npublic class OrderContext : DbContext\n{\n    public DbSet<Order> Orders { get; set; }\n    public DbSet<OrderItem> OrderItems { get; set; } // Only include order items for order processing\n}