using NCalc;\n\n// Store the formula in the database\nstring formula = "A + 1";\ndb.ExecuteNonQuery("INSERT INTO Formulas (Formula) VALUES (@formula)", new SqlParameter("@formula", formula));\n\n// Retrieve the formula and evaluate it\nstring retrievedFormula = db.ExecuteScalar("SELECT Formula FROM Formulas WHERE Id = 1").ToString();\nExpression exp = new Expression(retrievedFormula);\nexp.Parameters["A"] = 10; // Set the value of A\ndouble result = exp.Evaluate();