using System.Net.Http;\nusing System.Xml;\n\npublic class UspsAddressParser\n{\n    private const string ApiKey = "YOUR_API_KEY";\n    private const string ApiUrl = "https://secure.shippingapis.com/ShippingAPI.dll";\n\n    public Address ParseAddress(string inputAddress)\n    {\n        var request = new HttpRequestMessage(HttpMethod.Get, ApiUrl)\n        {\n            Content = new StringContent($"API=CityStateLookup&XML=<CityStateLookupRequest USERID='{ApiKey}'><ZipCode ID='0'><Zip5>{inputAddress}</Zip5></ZipCode></CityStateLookupRequest>")\n        };\n\n        var response = await request.GetResponseAsync();\n        var xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(await response.Content.ReadAsStringAsync());\n\n        var address = new Address();\n        address.Street = xmlDoc.SelectSingleNode("//Address/Address1").InnerText;\n        address.City = xmlDoc.SelectSingleNode("//Address/City").InnerText;\n        address.State = xmlDoc.SelectSingleNode("//Address/State").InnerText;\n        address.Zip = xmlDoc.SelectSingleNode("//Address/Zip5").InnerText;\n\n        return address;\n    }\n}\n\npublic class Address\n{\n    public string Street { get; set; }\n    public string City { get; set; }\n    public string State { get; set; }\n    public string Zip { get; set; }\n}