public class CustomActionResult : ActionResult\n{\n    private readonly object _model;\n\n    public CustomActionResult(object model)\n    {\n        _model = model;\n    }\n\n    public override void ExecuteResult(ControllerContext context)\n    {\n        // Your logic here\n        if (/* some condition */)\n        {\n            context.Controller.ViewData.Model = _model;\n            new ViewResult() { ViewName = "YourViewName" }.ExecuteResult(context);\n        }\n        else\n        {\n            // Redirect\n            context.HttpContext.Response.Redirect("YourRedirectUrl");\n        }\n    }\n}