using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Queue a task to the thread pool\n        ThreadPool.QueueUserWorkItem(state => {\n            Console.WriteLine("Task is running on a background thread.");\n            // Simulate a lengthy operation\n            Thread.Sleep(5000);\n            Console.WriteLine("Task is complete.");\n        });\n\n        Console.WriteLine("Main thread is continuing to run.");\n        Console.ReadLine();\n    }\n}