using Microsoft.Win32;\n\n// Get the auto-configured proxy Uri from the registry\nRegistryKey regKey = Registry.CurrentUser.OpenSubKey(@"Software\Microsoft\Windows\CurrentVersion\Internet Settings");\nstring autoConfigProxyUri = regKey.GetValue("AutoConfigURL") as string;\n\n// Create a new WebProxy instance\nWebProxy proxy = new WebProxy(new Uri(autoConfigProxyUri), true);\nproxy.Credentials = CredentialCache.DefaultNetworkCredentials;\n\n// Set the proxy as the default\nWebRequest.DefaultWebProxy = proxy;