// Create a connection string\nstring connectionString = "Data Source=myServerAddress;Initial Catalog=myDataBase;User Id=myUsername;Password=myPassword;";\n\n// Create a DataSet\nDataSet myDataSet = new DataSet();\n\n// Create a TableAdapter\nSqlDataAdapter myTableAdapter = new SqlDataAdapter("SELECT * FROM myTable", connectionString);\n\n// Fill the DataSet with data from the database\nmyTableAdapter.Fill(myDataSet, "myTable");\n\n// Create a BindingSource\nBindingSource myBindingSource = new BindingSource();\n\n// Bind the DataSet to the BindingSource\nmyBindingSource.DataSource = myDataSet;\nmyBindingSource.DataMember = "myTable";\n\n// Bind the BindingSource to a UI control (e.g., a DataGridView)\ndataGridView1.DataSource = myBindingSource;