protected void Page_Load(object sender, EventArgs e)\n{\n    Response.Headers["Cache-Control"] = "no-cache, no-store, must-revalidate";\n\n    if (!Page.IsPostBack)\n    {\n        // Load page state from session\n        if (Session["PageState"] != null)\n        {\n            // Load state from session\n            RestoreFromSession();\n        }\n        else\n        {\n            // Initialize page state\n            DataBindDropDowns();\n        }\n    }\n    else\n    {\n        // Re-load page state from session if necessary\n        if (Request.Params["__ViewState"] != null)\n        {\n            // Re-load state from session\n            RestoreFromSession();\n        }\n    }\n}