public class CustomPermissionProvider : IDynamicDataPermissionProvider\n{\n    public bool HasPermission(string tableName, string action)\n    {\n        var user = HttpContext.Current.User as CustomUser;\n        if (user == null) return false;\n\n        var propertyInfo = user.GetType().GetProperty($"CanView{tableName}");\n        if (propertyInfo == null) return false;\n\n        return (bool)propertyInfo.GetValue(user);\n    }\n}