using System.Security.Cryptography;\nusing System.Text;\n\npublic class MyService\n{\n    public string GetApiKey()\n    {\n        string encryptedValue = ConfigurationManager.AppSettings["MyApiKey"];\n        byte[] decryptedBytes = Decrypt(encryptedValue);\n        return Encoding.UTF8.GetString(decryptedBytes);\n    }\n\n    private byte[] Decrypt(string encryptedValue)\n    {\n        // Use a strong encryption algorithm like AES\n        Aes aes = Aes.Create();\n        aes.Key = GetEncryptionKey();\n        aes.IV = GetInitializationVector();\n        ICryptoTransform decryptor = aes.CreateDecryptor();\n        byte[] decryptedBytes = decryptor.TransformFinalBlock(Convert.FromBase64String(encryptedValue), 0, Convert.FromBase64String(encryptedValue).Length);\n        return decryptedBytes;\n    }\n}