using Microsoft.SharePoint;\nusing Microsoft.SharePoint.WebControls;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\n\nnamespace YourNamespace\n{\n    public class EmailEncoderControl : WebControl\n    {\n        protected override void Render(HtmlTextWriter writer)\n        {\n            // Get the email address from the Contact E-Mail Address site column\n            string emailAddress = SPContext.Current.Item["Contact_x0020_E_x002d_Mail_x0020_Address"] as string;\n\n            // Encode the email address using your JavaScript encoding method\n            string encodedEmailAddress = YourEncodingMethod(emailAddress);\n\n            // Render the encoded email link\n            writer.Write("<a href=\"javascript:sendEmail('" + encodedEmailAddress + "')\">Email " + SPContext.Current.Item["Name"] + "</a>");\n        }\n    }\n}