using Microsoft.Practices.EnterpriseLibrary.Common.Configuration;\nusing Microsoft.Practices.EnterpriseLibrary.Logging.Formatters;\nusing System.IO;\nusing System.Net.Mail;\nusing System.Text;\n\npublic class HtmlEmailFormatter : IEmailFormatter\n{\n    public void Format(StringBuilder message, EmailEntry entry)\n    {\n        string htmlBody = entry.Message;\n        string subject = entry.Title;\n\n        var mailMessage = new MailMessage();\n        mailMessage.Subject = subject;\n        mailMessage.IsBodyHtml = true;\n        mailMessage.Body = htmlBody;\n\n        using (var writer = new StringWriter())\n        {\n            writer.Write(mailMessage.Body);\n            message.Append(writer.ToString());\n        }\n    }\n}