using SharpSvn;\n\n// Create a new SvnClient instance\nusing (SvnClient client = new SvnClient())\n{\n    // Set the repository URL and the folder you want to read\n    string repositoryUrl = "https://your-svn-repo.com/svn/your-project";\n    string folderPath = "/trunk/source";\n\n    // Get the list of files in the folder\n    Collection<SvnListEventArgs> files;\n    client.GetList(repositoryUrl + folderPath, out files);\n\n    // Iterate over the files and print their paths\n    foreach (SvnListEventArgs file in files)\n    {\n        Console.WriteLine(file.Path);\n    }\n}