using System.Windows;\nusing System.Windows.Controls;\n\npublic partial class MainWindow : Window\n{\n    public MainWindow()\n    {\n        InitializeComponent();\n        \n        // Assuming you have a Grid named 'MainGrid' in your XAML\n        WebBrowser browser = new WebBrowser();\n        MainGrid.Children.Add(browser);\n        \n        // Navigate to the PDF file\n        browser.Navigate("file:///path/to/your/document.pdf");\n    }\n}