using System;\nusing System.Security;\nusing System.Security.Permissions;\nusing System.Threading;\n\nclass SandboxThread\n{\n    public static void RunThirdPartyScript(string script)\n    {\n        // Create a permission set with the desired permissions\n        PermissionSet permissionSet = new PermissionSet(PermissionState.None);\n        permissionSet.AddPermission(new WebPermission(NetworkAccess.Connect, "http://example.com"));\n        permissionSet.AddPermission(new WebPermission(NetworkAccess.Connect, "http://example.org"));\n\n        // Create a new thread with the restricted permission set\n        Thread thread = new Thread(() =>\n        {\n            // Run the third-party script in the sandboxed thread\n            script.Execute();\n        });\n        thread.SetAppDomainSecurity(permissionSet);\n\n        // Start the thread\n        thread.Start();\n    }\n}