using System.Data.OracleClient;\n\n// Create an Oracle connection and command\nOracleConnection conn = new OracleConnection("your connection string");\nOracleCommand cmd = new OracleCommand("SELECT TEXT FROM USER_SOURCE WHERE NAME = 'PROCEDURE_NAME' ORDER BY LINE", conn);\n\n// Execute the command and retrieve the results\nconn.Open();\nOracleDataReader reader = cmd.ExecuteReader();\nstring procedureBody = "";\nwhile (reader.Read())\n{\n    procedureBody += reader["TEXT"].ToString() + Environment.NewLine;\n}\nreader.Close();\nconn.Close();\n\n// Display the procedure body\nConsole.WriteLine(procedureBody);