using System;\nusing System.Data.SqlClient;\nusing System.Windows.Forms;\n\npublic class DataLinkForm : Form\n{\n    private TextBox txtServerName;\n    private TextBox txtDatabaseName;\n    private ComboBox cmbAuthentication;\n    private TextBox txtUsername;\n    private TextBox txtPassword;\n\n    public DataLinkForm()\n    {\n        // Initialize form controls\n        txtServerName = new TextBox();\n        txtDatabaseName = new TextBox();\n        cmbAuthentication = new ComboBox();\n        txtUsername = new TextBox();\n        txtPassword = new TextBox();\n\n        // Set up form layout\n        this.Controls.Add(txtServerName);\n        this.Controls.Add(txtDatabaseName);\n        this.Controls.Add(cmbAuthentication);\n        this.Controls.Add(txtUsername);\n        this.Controls.Add(txtPassword);\n\n        // Add event handler for authentication combo box\n        cmbAuthentication.SelectedIndexChanged += new EventHandler(cmbAuthentication_SelectedIndexChanged);\n    }\n\n    private void cmbAuthentication_SelectedIndexChanged(object sender, EventArgs e)\n    {\n        if (cmbAuthentication.SelectedIndex == 0) // Windows Authentication\n        {\n            txtUsername.Enabled = false;\n            txtPassword.Enabled = false;\n        }\n        else // SQL Server Authentication\n        {\n            txtUsername.Enabled = true;\n            txtPassword.Enabled = true;\n        }\n    }\n\n    private void btnConnect_Click(object sender, EventArgs e)\n    {\n        // Build connection string using SqlConnectionStringBuilder\n        SqlConnectionStringBuilder builder = new SqlConnectionStringBuilder();\n        builder.DataSource = txtServerName.Text;\n        builder.InitialCatalog = txtDatabaseName.Text;\n        builder.IntegratedSecurity = (cmbAuthentication.SelectedIndex == 0);\n\n        if (!builder.IntegratedSecurity)\n        {\n            builder.UserID = txtUsername.Text;\n            builder.Password = txtPassword.Text;\n        }\n\n        // Create a new SqlConnection object\n        using (SqlConnection connection = new SqlConnection(builder.ToString()))\n        {\n            try\n            {\n                connection.Open();\n                MessageBox.Show("Connected successfully!");\n            }\n            catch (SqlException ex)\n            {\n                MessageBox.Show(ex.Message);\n            }\n        }\n    }\n}