public class GuidSerializer : IDataServiceSerializer\n{\n    public void WriteObjectProperties(object element, ResourceType resourceType, Uri absoluteUri, string relativeUri, SyndicationItem item, DictionaryContent content)\n    {\n        if (element is UserDetails)\n        {\n            UserDetails userDetails = (UserDetails)element;\n            content.Add("UserId", userDetails.UserId.ToString());\n        }\n        else\n        {\n            base.WriteObjectProperties(element, resourceType, absoluteUri, relativeUri, item, content);\n        }\n    }\n}