List<string> GetPage(List<string> docList, List<int> pageBreakList, int pageNum)\n{\n    if (pageNum <= 0) pageNum = 1;\n    if (pageBreakList.Count == 0) return docList; // single page document\n\n    var startIndex = pageBreakList.Count >= pageNum - 1 ? \n                     pageBreakList[pageNum - 2] : \n                     pageBreakList.Last();\n\n    var endIndex = pageBreakList.Count >= pageNum ? \n                   pageBreakList[pageNum - 1] : \n                   docList.Count;\n\n    return docList.GetRange(startIndex, endIndex - startIndex);\n}