using System.Text.RegularExpressions;\n\n// Define the regular expression patterns\nstring capitalLettersPattern = @"^[A-Z]+$";\nstring digitsPattern = @"^[0-9]+$";\nstring noSpecialCharsPattern = @"^[a-zA-Z0-9]+$";\n\n// Create a TextBox and add a TextInput event handler\nTextBox textBox = new TextBox();\ntextBox.TextInput += (sender, e) => {\n    string input = e.Text;\n    if (!Regex.IsMatch(input, capitalLettersPattern)) {\n        e.Handled = true; // reject input\n    }\n};