using System.Reflection;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var assembly = Assembly.GetExecutingAssembly();\n        var version = assembly.GetName().Version;\n        var productName = ((AssemblyProductAttribute)assembly.GetCustomAttributes(typeof(AssemblyProductAttribute), false).FirstOrDefault())?.Product;\n\n        Console.WriteLine($"{productName} ({version.Major}.{version.Minor}.{version.Build}.{version.Revision})");\n    }\n}