protected void Page_Load(object sender, EventArgs e)\n{\n    // Get the nav element\n    HtmlGenericControl nav = (HtmlGenericControl)Master.FindControl("nav");\n\n    // Get the current page's URL\n    string currentPageUrl = Request.Url.AbsolutePath;\n\n    // Loop through each li element\n    foreach (HtmlGenericControl li in nav.Controls)\n    {\n        // Get the a element inside the li\n        HtmlAnchor a = (HtmlAnchor)li.FindControl("a");\n\n        // Check if the a element's href matches the current page's URL\n        if (a.HRef == currentPageUrl)\n        {\n            // Add the class attribute to the li element\n            li.Attributes.Add("class", "active");\n        }\n    }\n}