protected void Page_Load(object sender, EventArgs e)\n{\n    // Get the current page's URL\n    string currentPageUrl = Request.Url.AbsolutePath;\n\n    // Use jQuery to add the class attribute\n    string script = @"\n        $(document).ready(function() {\n            $('#nav li a[href=""" + currentPageUrl + @"""]').parent().addClass('active');\n        });\n    ";\n\n    // Register the script\n    ScriptManager.RegisterStartupScript(this, GetType(), "SetActiveClass", script, true);\n}