using System;\nusing System.Runtime.InteropServices;\nusing System.Runtime.InteropServices.ComTypes;\n\n// Assume obj is a COM object\nobject obj = ...;\n\n// Get the IDispatch interface\nIDispatch disp = (IDispatch)obj;\n\n// Get the ITypeInfo interface\nITypeInfo typeInfo;\ndisp.GetTypeInfo(0, LOCALE_SYSTEM_DEFAULT, out typeInfo);\n\n// Enumerate the members\nTYPEATTR typeAttr;\ntypeInfo.GetTypeAttr(out typeAttr);\nfor (int i = 0; i < typeAttr.cFuncs; i++)\n{\n    FUNCDESC funcDesc;\n    typeInfo.GetFuncDesc(i, out funcDesc);\n\n    // Get the member name\n    string name;\n    typeInfo.GetDocumentation(funcDesc.memid, out name, null, null, null);\n    Console.WriteLine(name);\n\n    // Get the member parameters\n    for (int j = 0; j < funcDesc.cParams; j++)\n    {\n        PARAMDESC paramDesc;\n        typeInfo.GetParamDesc(funcDesc.memid, j, out paramDesc);\n\n        // Get the parameter name\n        string paramName;\n        typeInfo.GetDocumentation(paramDesc.paramdesc.parmdesc.name, out paramName, null, null, null);\n        Console.WriteLine("  " + paramName);\n    }\n}