public delegate void ModifyUIAction(Action action);\n\npublic partial class MyForm : Form\n{\n    private readonly ModifyUIAction modifyUI;\n\n    public MyForm()\n    {\n        InitializeComponent();\n        modifyUI = new ModifyUIAction(ModifyUI);\n    }\n\n    private void ModifyUI(Action action)\n    {\n        if (InvokeRequired)\n        {\n            Invoke(modifyUI, action);\n        }\n        else\n        {\n            action();\n        }\n    }\n\n    // Usage:\n    public void UpdateLabel(string text)\n    {\n        modifyUI(() => label1.Text = text);\n    }\n}