public class Program\n{\n    public delegate string ValidationMessageDelegate(int min, int max);\n\n    public static string WeightInvalid(int min, int max)\n    {\n        return $"Weight must be between {min} and {max}.";\n    }\n\n    public static string HeightInvalid(int min, int max)\n    {\n        return $"Height must be between {min} and {max}.";\n    }\n\n    public static void Main()\n    {\n        ValidationMessageDelegate weightMessage = WeightInvalid;\n        ValidationMessageDelegate heightMessage = HeightInvalid;\n\n        Console.WriteLine(weightMessage(50, 100));\n        Console.WriteLine(heightMessage(150, 200));\n    }\n}