public class WikiRouteHandler : MvcRouteHandler\n{\n    protected override IHttpHandler GetHttpHandler(RequestContext requestContext)\n    {\n        var path = requestContext.HttpContext.Request.Url.AbsolutePath;\n        if (path.EndsWith("/wiki", StringComparison.OrdinalIgnoreCase))\n        {\n            requestContext.RouteData.Values["controller"] = "Wiki";\n            requestContext.RouteData.Values["action"] = "page";\n        }\n        return base.GetHttpHandler(requestContext);\n    }\n}