using System;\n\nclass ComObjectValidator\n{\n    public static bool IsComObjectRegistered(string progId)\n    {\n        Type type = Type.GetTypeFromProgID(progId);\n        return type != null;\n    }\n}\n\n// Usage:\nstring progId = "Foo.Bar";\nif (ComObjectValidator.IsComObjectRegistered(progId))\n{\n    // The COM object is registered, you can create an instance\n    Foo.Bar comObject = new Foo.Bar();\n}\nelse\n{\n    // The COM object is not registered\n    Console.WriteLine($"COM object {progId} is not registered.");\n}