using System.Drawing;\nusing System.Drawing.Imaging;\n\n// ...\n\n// Get the property items from the TIF image\nPropertyItem[] propertyItems = image1.PropertyItems;\n\n// Create a new list to store the compatible property items\nList<PropertyItem> compatiblePropertyItems = new List<PropertyItem>();\n\n// Iterate through the property items and filter out incompatible ones\nforeach (PropertyItem propertyItem in propertyItems)\n{\n    // Check if the property item is compatible with JPEG\n    if (propertyItem.Id == 0x010e || // Camera Make\n        propertyItem.Id == 0x010f || // Camera Model\n        propertyItem.Id == 0x0132 || // Date Taken\n        propertyItem.Id == 0x0112)   // Orientation\n    {\n        compatiblePropertyItems.Add(propertyItem);\n    }\n}\n\n// Set the compatible property items on the JPEG image\nforeach (PropertyItem propertyItem in compatiblePropertyItems)\n{\n    image2.SetPropertyItem(propertyItem);\n}\n\n// Save the JPEG image\nimage2.Save("C:\\1.jpg", ImageFormat.Jpeg);