using iTextSharp.text;\nusing iTextSharp.text.pdf;\n\n// ...\n\n// Load the PDF generated by Oracle Reports\nPdfReader reader = new PdfReader("path/to/report.pdf");\n\n// Create a new PDF document with black and white color space\nDocument document = new Document();\nPdfWriter writer = PdfWriter.GetInstance(document, new FileStream("path/to/blackandwhite.pdf", FileMode.Create));\ndocument.Open();\n\n// Set the color space to black and white\nwriter.SetColorSpace(PdfName.DEVICEGRAY);\n\n// Copy the pages from the original PDF to the new PDF\nint n = reader.NumberOfPages;\nfor (int i = 1; i <= n; i++) {\n    PdfImportedPage page = writer.GetImportedPage(reader, i);\n    document.Add(page);\n}\n\n// Close the documents\ndocument.Close();\nreader.Close();