using CrystalDecisions.CrystalReports.Engine;\n\n// Create a new ReportDocument object\nReportDocument report = new ReportDocument();\n\n// Load the report\nreport.Load("path/to/your/report.rpt");\n\n// Set the database logon information for the UAT DB\nreport.SetDatabaseLogon("UAT_DB_Server", "UAT_DB_Database", "UAT_DB_Username", "UAT_DB_Password");\n\n// Run the report\nreport.Refresh();