using Oracle.DataAccess.Client;\n\npublic class LoggingOracleConnection : OracleConnection\n{\n  public LoggingOracleConnection(string connectionString) : base(connectionString) { }\n\n  public override void Open()\n  {\n    base.Open();\n    LogSqlStatement("Connection opened");\n  }\n\n  public override OracleCommand CreateCommand()\n  {\n    OracleCommand cmd = base.CreateCommand();\n    LogSqlStatement(cmd.CommandText);\n    return cmd;\n  }\n\n  private void LogSqlStatement(string sql)\n  {\n    // Log the SQL statement to a file or database table\n  }\n}