using System;\nusing System.Text;\n\npublic static class UsernameScrambler\n{\n    private const int Shift = 3; // adjust to your liking\n\n    public static string ScrambleUsername(string username)\n    {\n        string[] parts = username.Split('.');\n        for (int i = 0; i < parts.Length; i++)\n        {\n            parts[i] = ScramblePart(parts[i]);\n        }\n        return string.Join(".", parts);\n    }\n\n    private static string ScramblePart(string part)\n    {\n        StringBuilder scrambled = new StringBuilder();\n        foreach (char c in part)\n        {\n            char shifted = (char)((c - 'a' + Shift) % 26 + 'a');\n            scrambled.Append(shifted);\n        }\n        return scrambled.ToString();\n    }\n}