// MEF example: Extensibility\n[Export(typeof(MyContract))]\npublic class MyComponent : MyContract { }\n\n[Import(typeof(MyContract))]\npublic class MyHost\n{\n    public void DoWork()\n    {\n        // MEF will compose the MyComponent instance\n        var component = new MyComponent();\n        // Use the component\n    }\n}\n\n// System.AddIn example: Add-in management\n[AddIn("MyAddIn")]\npublic class MyAddIn : AddInBase { }\n\npublic class MyHost\n{\n    public void LoadAddIn()\n    {\n        // Load the add-in using System.AddIn\n        var addIn = AddInStore.FindAddIns(typeof(MyAddIn), null);\n        // Activate and use the add-in\n    }\n}