var query = from a in List.All()\n            join b in List.All() on new { a.Value, a.Category, a.Description, a.Item } equals new { b.Value, b.Category, b.Description, b.Item } into temp\n            from b in temp.DefaultIfEmpty()\n            where a.Category == "General" \n               && a.Description == "Cover" \n               && a.EffectiveDate <= DateTime.Parse("2009-04-01 00:00:00") \n               && (b == null || b.EffectiveDate >= DateTime.Parse("2009-04-01 00:00:00")) \n               && a.Item == "Type"\n            group new { a, b } by new { a.Category, a.Description, a.Item, a.Value, a.Loading, a.MaxDiscount, a.SortOrder, a.EffectiveDate, a.ID } into g\n            select new\n            {\n                Category = g.Key.Category,\n                Description = g.Key.Description,\n                Item = g.Key.Item,\n                Value = g.Key.Value,\n                Loading = g.Key.Loading,\n                MaxDiscount = g.Key.MaxDiscount,\n                FromDate = g.Min(x => x.b == null ? (DateTime?)null : x.b.EffectiveDate),\n                EndDate = g.Key.EffectiveDate,\n                SortOrder = g.Key.SortOrder,\n                ID = g.Key.ID\n            }\n            .OrderBy(x => x.SortOrder);