using Microsoft.AnalysisServices.AdomdClient;\n\n// Create a connection to the OLAP cube\nAdomdConnection connection = new AdomdConnection("Data Source=<your_cube_name>;");\n\n// Create a query\nAdomdCommand command = new AdomdCommand("SELECT [Measures].[Sales] ON COLUMNS FROM [SalesCube]", connection);\n\n// Execute the query and retrieve the results\nAdomdDataReader reader = command.ExecuteReader();\n\n// Iterate over the results\nwhile (reader.Read())\n{\n    Console.WriteLine(reader[0].ToString());\n}