using iTextSharp.text.pdf;\n\npublic void AppendPdf(string srcFile, string destFile)\n{\n    using (PdfReader reader = new PdfReader(srcFile))\n    {\n        using (PdfReader reader2 = new PdfReader(destFile))\n        {\n            using (PdfCopy copy = new PdfCopy(new PdfDocument(), new FileStream(srcFile, FileMode.Append)))\n            {\n                copy.AddDocument(reader2);\n            }\n        }\n    }\n}