using System.Web.Caching;\n\npublic class OrderController : Controller\n{\n    [HttpGet]\n    public ActionResult GetOrder(int orderId)\n    {\n        // Check if order is cached\n        if (HttpRuntime.Cache["Order_" + orderId] != null)\n        {\n            return (Order)HttpRuntime.Cache["Order_" + orderId];\n        }\n        else\n        {\n            // Retrieve order from database and cache it\n            Order order = OrderRepository.GetOrder(orderId);\n            HttpRuntime.Cache.Insert("Order_" + orderId, order, null, DateTime.Now.AddHours(1), Cache.NoSlidingExpiration);\n            return order;\n        }\n    }\n}