// Server-side (ASP.NET)\nusing Microsoft.AspNetCore.SignalR;\n\npublic class StatusHub : Hub\n{\n    public void UpdateStatus(string message)\n    {\n        Clients.All.updateStatus(message);\n    }\n}\n\n// In your long-running process\npublic void MyLongRunningProcess()\n{\n    var hub = new StatusHub();\n    do\n    {\n        // Get data via web service\n        var data = GetData();\n        hub.UpdateStatus($"Processing {data.Count} items...");\n        // Process data...\n    } while (/* condition */);\n}\n\n// Client-side (JavaScript)\n<script src="https://cdn.jsdelivr.net/npm/@microsoft/signalr@5.0.4/dist/signalr.min.js"></script>\n<script>\n    var connection = new signalR.HubConnectionBuilder().withUrl("/statusHub").build();\n\n    connection.on("updateStatus", function (message) {\n        document.getElementById("myLabel").innerHTML = message;\n    });\n\n    connection.start().catch(function (err) {\n        return console.error(err.toString());\n    });\n</script>