using System;\nusing System.Web;\n\npublic class ConnectionPoolLogger : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.BeginRequest += new EventHandler(OnBeginRequest);\n    }\n\n    public void Dispose()\n    {\n    }\n\n    private void OnBeginRequest(object sender, EventArgs e)\n    {\n        HttpApplication app = (HttpApplication)sender;\n        HttpContext context = app.Context;\n\n        // Log connection pool information\n        string connectionString = context.Request.QueryString["connectionString"];\n        string applicationName = context.Request.ApplicationPath;\n\n        // Log database connections\n        string dbConnectionString = context.Request.QueryString["dbConnectionString"];\n        string dbApplicationName = context.Request.ApplicationPath;\n\n        // Log connection pool leak information\n        string leakInfo = context.Request.QueryString["leakInfo"];\n\n        // Log to file or database\n    }\n}