DataTable table = dataSet.Tables[0];\n\nforeach (Constraint constraint in table.Constraints)\n{\n    if (constraint.GetType() == typeof(UniqueConstraint))\n    {\n        UniqueConstraint uniqueConstraint = (UniqueConstraint)constraint;\n        if (uniqueConstraint.IsBroken())\n        {\n            Console.WriteLine($"Unique constraint {uniqueConstraint.ConstraintName} is broken.");\n            // check the column(s) involved in the constraint\n        }\n    }\n    else if (constraint.GetType() == typeof(ForeignKeyConstraint))\n    {\n        ForeignKeyConstraint foreignKeyConstraint = (ForeignKeyConstraint)constraint;\n        if (foreignKeyConstraint.IsBroken())\n        {\n            Console.WriteLine($"Foreign key constraint {foreignKeyConstraint.ConstraintName} is broken.");\n            // check the column(s) involved in the constraint\n        }\n    }\n}