using (SqlConnection con = new SqlConnection(System.Web.Configuration.WebConfigurationManager.ConnectionStrings["FAQ"].ToString()))\n{\n    con.Open();\n\n    using (SqlCommand cmd = new SqlCommand("SELECT FileData FROM Files WHERE Id = @id", con))\n    {\n        cmd.Parameters.AddWithValue("@id", id); // Replace with the actual ID of the file you want to retrieve\n\n        using (SqlDataReader reader = cmd.ExecuteReader())\n        {\n            if (reader.Read())\n            {\n                byte[] fileData = (byte[])reader["FileData"];\n                string filePath = "C:\\path\\to\\save\\file";\n                File.WriteAllBytes(filePath, fileData);\n            }\n        }\n    }\n}