using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nList<string> words = new List<string> { /* your 170 elements here */ };\n\nvar occurrences = words.GroupBy(w => w)\n                       .Select(g => new { Word = g.Key, Count = g.Count() })\n                       .ToList();\n\nforeach (var occurrence in occurrences)\n{\n    Console.Write($"{occurrence.Word} ({occurrence.Count}), ");\n}