var result = from p in db.Persons\n             let latestLogEntry = (from l in db.LogEntries\n                                   where l.PersonKey == p.Key\n                                   orderby l.EntryTime descending\n                                   select l).FirstOrDefault()\n             select new\n             {\n                 Person = p,\n                 LatestLogEntry = latestLogEntry\n             };