using OpenQA.Selenium;\nusing OpenQA.Selenium.Support.UI;\n\n// Assume you have an instance of IWebDriver (e.g., driver)\n\n// Select the country dropdown and select the USA option\nIWebElement countryDropdown = driver.FindElement(By.Id("countryDropdown"));\nSelectElement countrySelect = new SelectElement(countryDropdown);\ncountrySelect.SelectByText("USA");\n\n// Wait for the state dropdown list to be populated\nWebDriverWait wait = new WebDriverWait(driver, TimeSpan.FromSeconds(10));\nIWebElement stateDropdown = wait.Until(ExpectedConditions.ElementIsVisible(By.Id("stateDropdown")));\n\n// Now you can interact with the state dropdown list\nSelectElement stateSelect = new SelectElement(stateDropdown);\nstateSelect.SelectByText("California");