DataSet dataSet = ...; // load your dataset\n\nDataRow[] errorRows = dataSet.Tables[0].GetErrors();\n\nforeach (DataRow errorRow in errorRows)\n{\n    Console.WriteLine($"Error in row {errorRow.Table.Rows.IndexOf(errorRow)}:");\n    foreach (DataColumn column in errorRow.Table.Columns)\n    {\n        if (errorRow.HasErrors && errorRow.GetColumnError(column) != "")\n        {\n            Console.WriteLine($"  Column {column.ColumnName}: {errorRow.GetColumnError(column)}");\n        }\n    }\n}