using System;\nusing System.Data.SqlClient;\nusing System.IO;\n\n// Store report in database\npublic void StoreReport(string reportName, byte[] reportData)\n{\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        SqlCommand command = new SqlCommand("INSERT INTO Reports (ReportName, ReportData) VALUES (@ReportName, @ReportData)", connection);\n        command.Parameters.AddWithValue("@ReportName", reportName);\n        command.Parameters.AddWithValue("@ReportData", reportData);\n        command.ExecuteNonQuery();\n    }\n}\n\n// Retrieve report from database\npublic byte[] RetrieveReport(string reportName)\n{\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        SqlCommand command = new SqlCommand("SELECT ReportData FROM Reports WHERE ReportName = @ReportName", connection);\n        command.Parameters.AddWithValue("@ReportName", reportName);\n        SqlDataReader reader = command.ExecuteReader();\n        if (reader.Read())\n        {\n            byte[] reportData = (byte[])reader["ReportData"];\n            return reportData;\n        }\n        else\n        {\n            return null;\n        }\n    }\n}