using Castle.Components.Validator;\n\n// assuming you have a Subsonic generated class called "User"\npublic class UserValidator : AbstractValidator<User>\n{\n    public override bool IsValid(User instance)\n    {\n        // specify validation rules here\n        Validate(instance, "Name").IsNotEmpty().IsLength(1, 50);\n        Validate(instance, "Email").IsNotEmpty().IsEmail();\n\n        return base.IsValid(instance);\n    }\n}\n\n// usage\nvar user = new User();\nvar validator = new UserValidator();\nif (!validator.IsValid(user))\n{\n    // get error messages\n    var errors = validator.GetErrorSummary(user);\n}