using System;\nusing System.IO;\n\nclass PublishSite\n{\n    static void Main(string[] args)\n    {\n        // Set the source and destination directories\n        string sourceDir = @"C:\SourceSafe\MyProject";\n        string destDir = @"C:\DestinationServer\MySite";\n\n        // Use the Publish Wizard to publish the site\n        Microsoft.VisualStudio.Publish.PublishWizard wizard = new Microsoft.VisualStudio.Publish.PublishWizard();\n        wizard.SourceDirectory = sourceDir;\n        wizard.DestinationDirectory = destDir;\n        wizard.Publish();\n    }\n}