protected void Page_Load(object sender, EventArgs e)\n{\n    // Assuming you have a collection of items\n    List<MyItem> items = GetItemsFromDatabase();\n\n    // Create a new HtmlGenericControl for the unordered list\n    HtmlGenericControl ul = new HtmlGenericControl("ul");\n\n    // Iterate over your collection and create a new HtmlGenericControl for each list item\n    foreach (MyItem item in items)\n    {\n        HtmlGenericControl li = new HtmlGenericControl("li");\n        li.InnerText = item.Text;\n\n        // Create a new HtmlAnchor for the image link\n        HtmlAnchor a = new HtmlAnchor();\n        a.HRef = "#";\n\n        // Create a new HtmlImage for the image\n        HtmlImage img = new HtmlImage();\n        img.Src = item.ImageUrl;\n        a.Controls.Add(img);\n\n        // Add the anchor to the list item\n        li.Controls.Add(a);\n\n        // Add the list item to the unordered list\n        ul.Controls.Add(li);\n    }\n\n    // Add the unordered list to the page\n    this.Controls.Add(ul);\n}\n\npublic class MyItem\n{\n    public string Text { get; set; }\n    public string ImageUrl { get; set; }\n}